/**
 * **unique**
 *
 * Runtime utility which removes intersecting values from two sets and
 * provides a unique values contained in each set.
 */
export const unique = (...values) => {
    const u = [];
    for (const i of values.flat()) {
        if (!u.includes(i)) {
            u.push(i);
        }
    }
    return u;
};
