import { addFnToProps } from "src/runtime/index";
const fn = (kind) => (msg) => ({
    kind,
    msg,
    context: {}
});
const api = {
    kinded: (kind, msg) => fn(kind)(msg),
    asKind: (kind) => (msg, context) => ({ kind, msg, context: context || {} })
};
const basic = (msg) => ({
    kind: "unknown",
    msg,
    context: {}
});
export const TypedError = addFnToProps(api)(basic);
