import { asArray } from "src/runtime/index";
/**
 * **tuple**(value)
 *
 * Creates a discrete tuple.
 * ```ts
 * // [1,2,3]
 * const t1 = tuple(1,2,3);
 * const t2 = tuple([1,2,3]);
 * ```
 */
export const tuple = (...values) => {
    const arr = (values.length === 1
        ? values[0]
        : values);
    return asArray(arr);
};
