import { isErrorCondition, isObject } from "src/runtime/index";
/**
 * **hasIndexOf**(value, idx) => boolean
 *
 * A type guard which determines whether container passed in has
 * an explicit index.
 */
export const hasIndexOf = (value, idx) => {
    const result = isObject(value)
        ? String(idx) in value
        : Array.isArray(value)
            ? Number(idx) in value
            : false;
    return (isErrorCondition(result, "invalid-index")
        ? false
        : result);
};
