import { isDoneFn, ShapeApiImplementation, isArray, isSameTypeOf, isShape } from "src/runtime/index";
export const isTuple = (...tuple) => {
    const results = tuple
        .map(i => i(ShapeApiImplementation))
        .map(i => isDoneFn(i) ? i.done() : i);
    return (v) => {
        return isArray(v) &&
            v.length === results.length &&
            results.every(isShape) &&
            v.every((item, idx) => isSameTypeOf(results[idx])(item));
    };
};
