import { isTrue } from "src/runtime/index";
/**
 * **createFnWithProps**`(fn, props)`
 *
 * creates a strongly typed function along with properties.
 */
export const createFnWithProps = (fn, props, narrowing = false) => {
    let fnWithProps = fn;
    for (let prop of Object.keys(props)) {
        fnWithProps[prop] = props[prop];
    }
    return (isTrue(narrowing)
        ? fnWithProps
        : fnWithProps);
};
